import browser from "webextension-polyfill";
import { Response } from "@shared/constants/types";
import {
  DEV_SERVER,
  EXTERNAL_MSG_RESPONSE,
  EXTERNAL_MSG_REQUEST,
  EXTERNAL_SERVICE_TYPES,
  SERVICE_TYPES,
} from "../../constants/services";

interface Msg {
  [key: string]: any;
  type: EXTERNAL_SERVICE_TYPES | SERVICE_TYPES;
}

export const sendMessageToContentScript = (msg: Msg): Promise<any> => {
  /* 
    In the case of multiple calls coming in sequentially, we use this MESSAGE_ID to make sure we're responding to
    the appropriate message sender. Otherwise, we can run into race conditions where we simply resolve all 
    sent messages with the first thing that comes back.
  */
  const MESSAGE_ID = Date.now() + Math.random();

  window.postMessage(
    { source: EXTERNAL_MSG_REQUEST, messageId: MESSAGE_ID, ...msg },
    window.location.origin,
  );
  return new Promise((resolve) => {
    let requestTimeout: number | NodeJS.Timeout = 0;

    /*
      In the case that LOBSTR is not installed at all, any messages to
      background from @lobstrco/signer-extension-api will hang forever and not respond in any way.
      This is especially a problem for the isConnected method, because this is
      likely to be called in a situation where LOBSTR isn't installed.
      To prevent this, we add a timeout to automatically resolve in the event
      LOBSTR doesn't respond in a timely fashion to this method.
    */
    if (msg.type === EXTERNAL_SERVICE_TYPES.REQUEST_CONNECTION_STATUS) {
      requestTimeout = setTimeout(() => {
        resolve({ isConnected: false });
        window.removeEventListener("message", messageListener);
      }, 2000);
    }

    const messageListener = (event: { source: any; data: any }) => {
      // We only accept messages from ourselves
      if (event.source !== window) return;
      // Only respond to messages tagged as being from our content script
      if (event?.data?.source !== EXTERNAL_MSG_RESPONSE) return;
      // Only respond to messages that this instance of sendMessageToContentScript sent
      if (event?.data?.messagedId !== MESSAGE_ID) return;

      resolve(event.data);
      window.removeEventListener("message", messageListener);
      clearTimeout(requestTimeout);
    };
    window.addEventListener("message", messageListener, false);
  });
};

export const sendMessageToBackground = async (msg: Msg): Promise<Response> => {
  let res;
  if (DEV_SERVER) {
    // treat this as an external call because we're making the call from the browser, not the popup
    res = await sendMessageToContentScript(msg);
  } else {
    res = await browser.runtime.sendMessage(msg);
  }

  return res;
};

import { requestPublicKey } from "@shared/api/external";
import { GetPublicKeyResponse } from "@shared/constants/types";
import { CONNECTION_KEY } from "@shared/constants/services";
import { isBrowser } from ".";

const saveConnectionKey = (connectionKey: string) => {
  window?.sessionStorage?.setItem(CONNECTION_KEY, connectionKey);
};

export const getPublicKey = async (): Promise<string> => {
  if (!isBrowser) {
    return "";
  }

  const { publicKey, connectionKey }: GetPublicKeyResponse =
    await requestPublicKey();

  saveConnectionKey(connectionKey);
  return publicKey;
};

import { GetPublicKeyResponse } from "@shared/constants/types";
import { EXTERNAL_SERVICE_TYPES } from "../constants/services";
import { sendMessageToContentScript } from "./helpers/extensionMessaging";

export const requestPublicKey = async (): Promise<GetPublicKeyResponse> => {
  let response = { publicKey: "", error: "", connectionKey: "" };
  try {
    response = await sendMessageToContentScript({
      type: EXTERNAL_SERVICE_TYPES.REQUEST_ACCESS,
    });
  } catch (e) {
    console.error(e);
  }

  const { publicKey, connectionKey, error } = response;

  if (error) {
    throw error;
  }
  return { publicKey, connectionKey };
};

export const signTransaction = async (
  transactionXdr: string,
  connectionKey: string,
): Promise<string> => {
  let response = { signedTransaction: "", error: "" };
  try {
    response = await sendMessageToContentScript({
      transactionXdr,
      connectionKey,
      type: EXTERNAL_SERVICE_TYPES.SIGN_TRANSACTION,
    });
  } catch (e) {
    console.error(e);
    throw e;
  }
  const { signedTransaction, error } = response;

  if (error) {
    throw error;
  }
  return signedTransaction;
};

export const requestConnectionStatus = async (): Promise<boolean> => {
  let response = {
    isConnected: false,
  };

  try {
    response = await sendMessageToContentScript({
      type: EXTERNAL_SERVICE_TYPES.REQUEST_CONNECTION_STATUS,
    });
  } catch (e) {
    console.error(e);
  }

  return response.isConnected;
};

import { signTransaction as signTransactionService } from "@shared/api/external";
import { CONNECTION_KEY } from "@shared/constants/services";
import { isBrowser } from "./index";

const getConnectionKey = () =>
  window?.sessionStorage?.getItem(CONNECTION_KEY) || "";

export const signTransaction = async (
  transactionXdr: string,
): Promise<string> => {
  if (!isBrowser) {
    return "";
  }

  const connectionKey = getConnectionKey();

  return await signTransactionService(transactionXdr, connectionKey);
};

import { requestConnectionStatus } from "@shared/api/external";
import { isBrowser } from "./index";

export const isConnected = async (): Promise<boolean> => {
  if (!isBrowser) {
    return false;
  }

  if (window.lobstrSignerExtension) {
    return window.lobstrSignerExtension;
  }

  return await requestConnectionStatus();
};

import { getPublicKey } from "./getPublicKey";
import { signTransaction } from "./signTransaction";
import { isConnected } from "./isConnected";

export const isBrowser = typeof window !== "undefined";

export { getPublicKey, signTransaction, isConnected };
export default {
  getPublicKey,
  signTransaction,
  isConnected,
};
